INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (1, 'Bulbasaur', 'Grass', 'Poison', 45, 49, 49, 65, 45, 'A strange seed was planted on its back at birth. The plant sprouts and grows with this POKéMON.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (4, 'Charmander', 'Fire', NULL, 39, 52, 43, 60, 65, 'Obviously prefers hot places. When it rains, steam is said to spout from the tip of its tail.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (7, 'Squirtle', 'Water', NULL, 44, 48, 65, 50, 43, 'After birth, its back swells and hardens into a shell. Powerfully sprays foam from its mouth.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (10, 'Caterpie', 'Bug', NULL, 45, 30, 35, 20, 45, 'Its short feet are tipped with suction pads that enable it to tirelessly climb slopes and walls.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (13, 'Weedle', 'Bug', 'Poison', 40, 35, 30, 20, 50, 'Often found in forests, eating leaves. It has a sharp venomous stinger on its head.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (16, 'Pidgey', 'Normal', 'Flying', 40, 45, 40, 35, 56, 'A common sight in forests and woods. It flaps its wings at ground level to kick up blinding sand.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (19, 'Rattata', 'Normal', NULL, 30, 56, 35, 25, 72, 'Bites anything when it attacks. Small and very quick, it is a common sight in many places.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (21, 'Spearow', 'Normal', 'Flying', 40, 60, 30, 31, 70, 'It flaps its small wings busily to fly. Using its beak, it searches in grass for prey.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (23, 'Ekans', 'Poison', NULL, 35, 60, 44, 40, 55, 'Moves silently and stealthily. Eats the eggs of birds, such as PIDGEY and SPEAROW, whole.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (27, 'Sandshrew', 'Ground', NULL, 50, 75, 85, 20, 40, 'Burrows deep underground in arid locations far from water. It only emerges to hunt for food.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (29, 'Nidoran-f', 'Poison', NULL, 55, 47, 52, 40, 41, 'Although small, its venomous barbs render this POKéMON dangerous. The female has smaller horns.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (32, 'Nidoran-m', 'Poison', NULL, 46, 57, 40, 40, 50, 'Stiffens its ears to sense danger. The larger its horns, the more powerful its secreted venom.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (37, 'Vulpix', 'Fire', NULL, 38, 41, 40, 50, 65, 'At the time of birth, it has just one tail. The tail splits from its tip as it grows older.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (41, 'Zubat', 'Poison', 'Flying', 40, 45, 35, 30, 55, 'Forms colonies in perpetually dark places. Uses ultrasonic waves to identify and approach targets.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (43, 'Oddish', 'Grass', 'Poison', 45, 50, 55, 75, 30, 'During the day, it keeps its face buried in the ground. At night, it wanders around sowing its seeds.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (46, 'Paras', 'Bug', 'Grass', 35, 70, 55, 45, 25, 'Burrows to suck tree roots. The mushrooms on its back grow by draw­ ing nutrients from the bug host.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (48, 'Venonat', 'Bug', 'Poison', 60, 55, 50, 40, 45, 'Lives in the shadows of tall trees where it eats insects. It is attracted by light at night.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (50, 'Diglett', 'Ground', NULL, 10, 55, 25, 35, 95, 'Lives about one yard underground where it feeds on plant roots. It sometimes appears above ground.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (52, 'Meowth', 'Normal', NULL, 40, 45, 35, 40, 90, 'It washes its face regularly to keep the coin on its forehead spotless. It doesn’t get along with Galarian Meowth.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (54, 'Psyduck', 'Water', NULL, 50, 52, 48, 65, 55, 'While lulling its enemies with its vacant look, this wily POKéMON will use psychokinetic powers.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (56, 'Mankey', 'Fighting', NULL, 40, 80, 35, 35, 70, 'Extremely quick to anger. It could be docile one moment then thrashing away the next instant.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (58, 'Growlithe', 'Fire', NULL, 55, 70, 45, 70, 60, 'Very protective of its territory. It will bark and bite to repel intruders from its space.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (60, 'Poliwag', 'Water', NULL, 40, 50, 40, 40, 90, 'Its newly grown legs prevent it from running. It appears to prefer swimming than trying to stand.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (63, 'Abra', 'Psychic', NULL, 25, 20, 15, 105, 90, 'Using its ability to read minds, it will identify impending danger and TELEPORT to safety.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (66, 'Machop', 'Fighting', NULL, 70, 80, 50, 35, 35, 'Loves to build its muscles. It trains in all styles of martial arts to become even stronger.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (69, 'Bellsprout', 'Grass', 'Poison', 50, 75, 35, 70, 40, 'A carnivorous POKéMON that traps and eats bugs. It uses its root feet to soak up needed moisture.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (72, 'Tentacool', 'Water', 'Poison', 40, 40, 35, 50, 70, 'Drifts in shallow seas. Anglers who hook them by accident are often punished by its stinging acid.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (74, 'Geodude', 'Rock', 'Ground', 40, 80, 100, 30, 20, 'Found in fields and mountains. Mistaking them for boulders, people often step or trip on them.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (3, 'Venusaur', 'Grass', 'Poison', 80, 82, 83, 100, 80, 'The plant blooms when it is absorbing solar energy. It stays on the move to seek sunlight.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (5, 'Charmeleon', 'Fire', NULL, 58, 64, 58, 80, 80, 'When it swings its burning tail, it elevates the temperature to unbearably high levels.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (6, 'Charizard', 'Fire', 'Flying', 78, 84, 78, 109, 100, 'Spits fire that is hot enough to melt boulders. Known to cause forest fires unintentionally.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (8, 'Wartortle', 'Water', NULL, 59, 63, 80, 65, 58, 'Often hides in water to stalk unwary prey. For swimming fast, it moves its ears to maintain balance.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (9, 'Blastoise', 'Water', NULL, 79, 83, 100, 85, 78, 'A brutal POKéMON with pressurized water jets on its shell. They are used for high speed tackles.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (11, 'Metapod', 'Bug', NULL, 50, 20, 55, 25, 30, 'This POKéMON is vulnerable to attack while its shell is soft, exposing its weak and tender body.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (12, 'Butterfree', 'Bug', 'Flying', 60, 45, 50, 90, 70, 'In battle, it flaps its wings at high speed to release highly toxic dust into the air.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (14, 'Kakuna', 'Bug', 'Poison', 45, 25, 50, 25, 35, 'Almost incapable of moving, this POKéMON can only harden its shell to protect itself from predators.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (15, 'Beedrill', 'Bug', 'Poison', 65, 90, 40, 45, 75, 'It has three poisonous stingers on its forelegs and its tail. They are used to jab its enemy repeatedly.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (17, 'Pidgeotto', 'Normal', 'Flying', 63, 60, 55, 50, 71, 'Very protective of its sprawling territorial area, this POKéMON will fiercely peck at any intruder.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (18, 'Pidgeot', 'Normal', 'Flying', 83, 80, 75, 70, 101, 'When hunting, it skims the surface of water at high speed to pick off unwary prey such as MAGIKARP.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (20, 'Raticate', 'Normal', NULL, 55, 81, 60, 50, 97, 'It uses its whis­ kers to maintain its balance. It apparently slows down if they are cut off.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (22, 'Fearow', 'Normal', 'Flying', 65, 90, 65, 61, 100, 'With its huge and magnificent wings, it can keep aloft without ever having to land for rest.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (24, 'Arbok', 'Poison', NULL, 60, 95, 69, 65, 80, 'It is rumored that the ferocious warning markings on its belly differ from area to area.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (25, 'Pikachu', 'Electric', NULL, 35, 55, 40, 50, 90, 'When several of these POKéMON gather, their electricity could build and cause lightning storms.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (26, 'Raichu', 'Electric', NULL, 60, 90, 55, 90, 110, 'Its long tail serves as a ground to protect itself from its own high-voltage power.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (28, 'Sandslash', 'Ground', NULL, 75, 100, 110, 45, 65, 'Curls up into a spiny ball when threatened. It can roll while curled up to attack or escape.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (30, 'Nidorina', 'Poison', NULL, 70, 62, 67, 55, 56, 'The female''s horn develops slowly. Prefers physical attacks such as clawing and biting.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (31, 'Nidoqueen', 'Poison', 'Ground', 90, 92, 87, 75, 76, 'Its hard scales provide strong protection. It uses its hefty bulk to execute powerful moves.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (33, 'Nidorino', 'Poison', NULL, 61, 72, 57, 55, 65, 'An aggressive POKéMON that is quick to attack. The horn on its head secretes a powerful venom.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (34, 'Nidoking', 'Poison', 'Ground', 81, 102, 77, 85, 85, 'It uses its powerful tail in battle to smash, constrict, then break the prey''s bones.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (35, 'Clefairy', 'Fairy', NULL, 70, 45, 48, 60, 35, 'Its magical and cute appeal has many admirers. It is rare and found only in certain areas.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (36, 'Clefable', 'Fairy', NULL, 95, 70, 73, 95, 60, 'A timid fairy POKéMON that is rarely seen. It will run and hide the moment it senses people.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (38, 'Ninetales', 'Fire', NULL, 73, 76, 75, 81, 100, 'Very smart and very vengeful. Grabbing one of its many tails could result in a 1000-year curse.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (39, 'Jigglypuff', 'Normal', 'Fairy', 115, 45, 20, 45, 20, 'When its huge eyes light up, it sings a mysteriously soothing melody that lulls its enemies to sleep.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (40, 'Wigglytuff', 'Normal', 'Fairy', 140, 70, 45, 85, 45, 'The body is soft and rubbery. When angered, it will suck in air and inflate itself to an enormous size.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (42, 'Golbat', 'Poison', 'Flying', 75, 80, 70, 65, 90, 'Once it strikes, it will not stop draining energy from the victim even if it gets too heavy to fly.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (44, 'Gloom', 'Grass', 'Poison', 60, 65, 70, 85, 40, 'The fluid that oozes from its mouth isn''t drool. It is a nectar that is used to attract prey.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (45, 'Vileplume', 'Grass', 'Poison', 75, 80, 85, 110, 50, 'It has the world’s largest petals. With every step, the petals shake out heavy clouds of toxic pollen.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (47, 'Parasect', 'Bug', 'Grass', 60, 95, 80, 60, 30, 'A host-parasite pair in which the parasite mushroom has taken over the host bug. Prefers damp places.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (49, 'Venomoth', 'Bug', 'Poison', 70, 65, 60, 90, 90, 'The dustlike scales covering its wings are color-coded to indicate the kinds of poison it has.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (51, 'Dugtrio', 'Ground', NULL, 35, 100, 50, 50, 120, 'A team of DIGLETT triplets. It triggers huge earthquakes by burrowing 60 miles underground.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (53, 'Persian', 'Normal', NULL, 65, 70, 60, 65, 115, 'Although its fur has many admirers, it is tough to raise as a pet because of its fickle meanness.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (55, 'Golduck', 'Water', NULL, 80, 82, 78, 95, 85, 'Often seen swim­ ming elegantly by lake shores. It is often mistaken for the Japanese monster, Kappa.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (57, 'Primeape', 'Fighting', NULL, 65, 105, 60, 60, 95, 'Always furious and tenacious to boot. It will not abandon chasing its quarry until it is caught.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (59, 'Arcanine', 'Fire', NULL, 90, 110, 80, 100, 95, 'A POKéMON that has been admired since the past for its beauty. It runs agilely as if on wings.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (61, 'Poliwhirl', 'Water', NULL, 65, 65, 65, 50, 90, 'Capable of living in or out of water. When out of water, it sweats to keep its body slimy.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (62, 'Poliwrath', 'Water', 'Fighting', 90, 95, 95, 70, 70, 'An adept swimmer at both the front crawl and breast stroke. Easily overtakes the best human swimmers.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (64, 'Kadabra', 'Psychic', NULL, 40, 35, 30, 120, 105, 'It emits special alpha waves from its body that induce headaches just by being close by.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (65, 'Alakazam', 'Psychic', NULL, 55, 50, 45, 135, 120, 'Its brain can out­ perform a super­ computer. Its intelligence quotient is said to be 5,000.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (67, 'Machoke', 'Fighting', NULL, 80, 100, 70, 50, 45, 'Its muscular body is so powerful, it must wear a power save belt to be able to regulate its motions.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (68, 'Machamp', 'Fighting', NULL, 90, 130, 80, 65, 55, 'Using its heavy muscles, it throws powerful punches that can send the victim clear over the horizon.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (70, 'Weepinbell', 'Grass', 'Poison', 65, 90, 50, 85, 55, 'It spits out POISONPOWDER to immobilize the enemy and then finishes it with a spray of ACID.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (71, 'Victreebel', 'Grass', 'Poison', 80, 105, 65, 100, 70, 'Said to live in huge colonies deep in jungles, although no one has ever returned from there.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (73, 'Tentacruel', 'Water', 'Poison', 80, 70, 65, 80, 100, 'The tentacles are normally kept short. On hunts, they are extended to ensnare and immobilize prey.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (75, 'Graveler', 'Rock', 'Ground', 55, 95, 115, 45, 35, 'Rolls down slopes to move. It rolls over any obstacle without slowing or changing its direction.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (77, 'Ponyta', 'Fire', NULL, 50, 85, 55, 65, 90, 'Its hooves are 10 times harder than diamonds. It can trample anything completely flat in little time.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (79, 'Slowpoke', 'Water', 'Psychic', 90, 65, 65, 40, 15, 'Incredibly slow and dopey. It takes 5 seconds for it to feel pain when under attack.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (81, 'Magnemite', 'Electric', 'Steel', 25, 35, 70, 95, 45, 'Uses anti-gravity to stay suspended. Appears without warning and uses THUNDER WAVE and similar moves.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (83, 'Farfetchd', 'Normal', 'Flying', 52, 90, 55, 58, 60, 'The sprig of green onions it holds is its weapon. It is used much like a metal sword.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (84, 'Doduo', 'Normal', 'Flying', 35, 85, 45, 35, 75, 'A bird that makes up for its poor flying with its fast foot speed. Leaves giant footprints.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (86, 'Seel', 'Water', NULL, 65, 45, 55, 45, 45, 'The protruding horn on its head is very hard. It is used for bashing through thick ice.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (88, 'Grimer', 'Poison', NULL, 80, 80, 50, 40, 25, 'Appears in filthy areas. Thrives by sucking up polluted sludge that is pumped out of factories.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (90, 'Shellder', 'Water', NULL, 30, 65, 100, 45, 40, 'Its hard shell repels any kind of attack. It is vulnerable only when its shell is open.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (92, 'Gastly', 'Ghost', 'Poison', 30, 35, 30, 100, 80, 'Almost invisible, this gaseous POKéMON cloaks the target and puts it to sleep without notice.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (95, 'Onix', 'Rock', 'Ground', 35, 45, 160, 30, 70, 'As it grows, the stone portions of its body harden to become similar to a diamond, but colored black.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (96, 'Drowzee', 'Psychic', NULL, 60, 48, 45, 43, 42, 'Puts enemies to sleep then eats their dreams. Occasionally gets sick from eating bad dreams.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (98, 'Krabby', 'Water', NULL, 30, 105, 90, 25, 50, 'Its pincers are not only powerful weapons, they are used for balance when walking sideways.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (100, 'Voltorb', 'Electric', NULL, 40, 30, 50, 55, 100, 'Usually found in power plants. Easily mistaken for a POKé BALL, they have zapped many people.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (102, 'Exeggcute', 'Grass', 'Psychic', 60, 40, 80, 60, 40, 'Often mistaken for eggs. When disturbed, they quickly gather and attack in swarms.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (104, 'Cubone', 'Ground', NULL, 50, 50, 95, 40, 35, 'Because it never removes its skull helmet, no one has ever seen this POKéMON''s real face.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (108, 'Lickitung', 'Normal', NULL, 90, 55, 75, 60, 30, 'Its tongue can be extended like a chameleon''s. It leaves a tingling sensation when it licks enemies.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (109, 'Koffing', 'Poison', NULL, 40, 65, 95, 60, 35, 'Because it stores several kinds of toxic gases in its body, it is prone to exploding without warning.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (111, 'Rhyhorn', 'Ground', 'Rock', 80, 85, 95, 30, 25, 'A POKéMON with a one-track mind. Once it charges,  it won''t stop running until it falls asleep.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (114, 'Tangela', 'Grass', NULL, 65, 55, 115, 100, 60, 'The whole body is swathed with wide vines that are similar to sea­ weed. Its vines shake as it walks.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (115, 'Kangaskhan', 'Normal', NULL, 105, 95, 80, 40, 90, 'The infant rarely ventures out of its mother''s protective pouch until it is 3 years old.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (116, 'Horsea', 'Water', NULL, 30, 40, 70, 70, 60, 'Known to shoot down flying bugs with precision blasts of ink from the surface of the water.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (118, 'Goldeen', 'Water', NULL, 45, 67, 60, 35, 63, 'Its tail fin billows like an elegant ballroom dress, giving it the nickname of the Water Queen.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (120, 'Staryu', 'Water', NULL, 30, 45, 55, 70, 85, 'If its body is torn, it can grow back if the red core remains. The core flashes at midnight.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (123, 'Scyther', 'Bug', 'Flying', 70, 110, 80, 55, 105, 'With ninja-like agility and speed, it can create the illusion that there is more than one.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (127, 'Pinsir', 'Bug', NULL, 65, 125, 100, 55, 85, 'If it fails to crush the victim in its pincers, it will swing it around and toss it hard.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (128, 'Tauros', 'Normal', NULL, 75, 100, 95, 40, 110, 'When it targets an enemy, it charges furiously while whipping its body with its long tails.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (129, 'Magikarp', 'Water', NULL, 20, 10, 55, 15, 80, 'In the distant past, it was somewhat stronger than the horribly weak descendants that exist today.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (131, 'Lapras', 'Water', 'Ice', 130, 85, 80, 85, 60, 'A POKéMON that has been over­ hunted almost to extinction. It can ferry people across the water.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (132, 'Ditto', 'Normal', NULL, 48, 48, 48, 48, 48, 'Capable of copying an enemy''s genetic code to instantly transform itself into a duplicate of the enemy.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (133, 'Eevee', 'Normal', NULL, 55, 55, 50, 45, 55, 'Its genetic code is irregular. It may mutate if it is exposed to radiation from element STONEs.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (137, 'Porygon', 'Normal', NULL, 65, 60, 70, 85, 40, 'A POKéMON that consists entirely of programming code. Capable of moving freely in cyberspace.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (138, 'Omanyte', 'Rock', 'Water', 35, 40, 100, 90, 35, 'Although long extinct, in rare cases, it can be genetically resurrected from fossils.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (140, 'Kabuto', 'Rock', 'Water', 30, 80, 90, 55, 55, 'A POKéMON that was resurrected from a fossil found in what was once the ocean floor eons ago.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (142, 'Aerodactyl', 'Rock', 'Flying', 80, 105, 65, 60, 130, 'A Pokémon that roamed the skies in the dinosaur era. Its teeth are like saw blades.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (144, 'Articuno', 'Ice', 'Flying', 90, 85, 100, 95, 85, 'A legendary bird POKéMON that is said to appear to doomed people who are lost in icy mountains.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (145, 'Zapdos', 'Electric', 'Flying', 90, 90, 85, 125, 100, 'A legendary bird POKéMON that is said to appear from clouds while dropping enormous lightning bolts.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (146, 'Moltres', 'Fire', 'Flying', 90, 100, 90, 125, 90, 'Known as the legendary bird of fire. Every flap of its wings creates a dazzling flash of flames.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (147, 'Dratini', 'Dragon', NULL, 41, 64, 45, 50, 50, 'Long considered a mythical POKéMON until recently when a small colony was found living underwater.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (150, 'Mewtwo', 'Psychic', NULL, 106, 110, 90, 154, 130, 'It was created by a scientist after years of horrific gene splicing and DNA engineering experiments.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (78, 'Rapidash', 'Fire', NULL, 65, 100, 70, 80, 105, 'Very competitive, this POKéMON will chase anything that moves fast in the hopes of racing it.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (80, 'Slowbro', 'Water', 'Psychic', 95, 75, 110, 100, 30, 'The SHELLDER that is latched onto SLOWPOKE''s tail is said to feed on the host''s left over scraps.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (82, 'Magneton', 'Electric', 'Steel', 50, 60, 95, 120, 70, 'Formed by several MAGNEMITEs linked together. They frequently appear when sunspots flare up.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (85, 'Dodrio', 'Normal', 'Flying', 60, 110, 70, 60, 110, 'Uses its three brains to execute complex plans. While two heads sleep, one head stays awake.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (87, 'Dewgong', 'Water', 'Ice', 90, 70, 80, 70, 70, 'Stores thermal energy in its body. Swims at a steady 8 knots even in intensely cold waters.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (89, 'Muk', 'Poison', NULL, 105, 105, 75, 65, 50, 'Thickly covered with a filthy, vile sludge. It is so toxic, even its footprints contain poison.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (91, 'Cloyster', 'Water', 'Ice', 50, 95, 180, 85, 70, 'When attacked, it launches its horns in quick volleys. Its innards have never been seen.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (93, 'Haunter', 'Ghost', 'Poison', 45, 50, 45, 115, 95, 'Because of its ability to slip through block walls, it is said to be from an­ other dimension.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (94, 'Gengar', 'Ghost', 'Poison', 60, 65, 60, 130, 110, 'Under a full moon, this POKéMON likes to mimic the shadows of people and laugh at their fright.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (97, 'Hypno', 'Psychic', NULL, 85, 73, 70, 73, 67, 'When it locks eyes with an enemy, it will use a mix of PSI moves such as HYPNOSIS and CONFUSION.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (99, 'Kingler', 'Water', NULL, 55, 130, 115, 50, 75, 'The large pincer has 10000 hp of crushing power. However, its huge size makes it unwieldy to use.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (101, 'Electrode', 'Electric', NULL, 60, 50, 70, 80, 150, 'It stores electric energy under very high pressure. It often explodes with little or no provocation.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (103, 'Exeggutor', 'Grass', 'Psychic', 95, 95, 85, 125, 55, 'Legend has it that on rare occasions, one of its heads will drop off and continue on as an EXEGGCUTE.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (105, 'Marowak', 'Ground', NULL, 60, 80, 110, 50, 45, 'The bone it holds is its key weapon. It throws the bone skillfully like a boomerang to KO targets.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (106, 'Hitmonlee', 'Fighting', NULL, 50, 120, 53, 35, 87, 'When in a hurry, its legs lengthen progressively. It runs smoothly with extra long, loping strides.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (107, 'Hitmonchan', 'Fighting', NULL, 50, 105, 79, 35, 76, 'While apparently doing nothing, it fires punches in lightning fast volleys that are impossible to see.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (110, 'Weezing', 'Poison', NULL, 65, 90, 120, 85, 60, 'Where two kinds of poison gases meet, 2 KOFFINGs can fuse into a WEEZING over many years.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (112, 'Rhydon', 'Ground', 'Rock', 105, 130, 120, 45, 40, 'Protected by an armor-like hide, it is capable of living in molten lava of 3,600 degrees.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (113, 'Chansey', 'Normal', NULL, 250, 5, 5, 35, 50, 'A rare and elusive POKéMON that is said to bring happiness to those who manage to get it.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (117, 'Seadra', 'Water', NULL, 55, 65, 95, 95, 85, 'Capable of swim­ ming backwards by rapidly flapping its wing-like pectoral fins and stout tail.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (119, 'Seaking', 'Water', NULL, 80, 92, 65, 65, 68, 'In the autumn spawning season, they can be seen swimming powerfully up rivers and creeks.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (121, 'Starmie', 'Water', 'Psychic', 60, 75, 85, 100, 115, 'Its central core glows with the seven colors of the rainbow. Some people value the core as a gem.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (122, 'Mr-mime', 'Psychic', 'Fairy', 40, 45, 65, 100, 90, 'If interrupted while it is miming, it will slap around the offender with its broad hands.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (124, 'Jynx', 'Ice', 'Psychic', 65, 50, 35, 115, 95, 'It seductively wiggles its hips as it walks. It can cause people to dance in unison with it.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (125, 'Electabuzz', 'Electric', NULL, 65, 83, 57, 95, 105, 'Normally found near power plants, they can wander away and cause major blackouts in cities.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (126, 'Magmar', 'Fire', NULL, 65, 95, 57, 100, 93, 'Its body always burns with an orange glow that enables it to hide perfectly among flames.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (130, 'Gyarados', 'Water', 'Flying', 95, 125, 79, 60, 81, 'Once it begins to rampage, a GYARADOS will burn everything down, even in a harsh storm.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (134, 'Vaporeon', 'Water', NULL, 130, 65, 60, 110, 65, 'Lives close to water. Its long tail is ridged with a fin which is often mistaken for a mermaid''s.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (135, 'Jolteon', 'Electric', NULL, 65, 65, 60, 110, 130, 'It accumulates negative ions in the atmosphere to blast out 10000- volt lightning bolts.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (136, 'Flareon', 'Fire', NULL, 65, 130, 60, 95, 65, 'When storing thermal energy in its body, its temperature could soar to over 1600 degrees.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (139, 'Omastar', 'Rock', 'Water', 70, 60, 125, 115, 55, 'A prehistoric POKéMON that died out when its heavy shell made it impossible to catch prey.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (141, 'Kabutops', 'Rock', 'Water', 60, 115, 105, 65, 80, 'Its sleek shape is perfect for swim­ ming. It slashes prey with its claws and drains the body fluids.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (143, 'Snorlax', 'Normal', NULL, 160, 110, 65, 65, 30, 'Very lazy. Just eats and sleeps. As its rotund bulk builds, it becomes steadily more slothful.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (148, 'Dragonair', 'Dragon', NULL, 61, 84, 65, 70, 70, 'A mystical POKéMON that exudes a gentle aura. Has the ability to change climate conditions.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (149, 'Dragonite', 'Dragon', 'Flying', 91, 134, 95, 100, 80, 'An extremely rarely seen marine POKéMON. Its intelligence is said to match that of humans.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (151, 'Mew', 'Psychic', NULL, 100, 100, 100, 100, 100, 'So rare that it is still said to be a mirage by many experts. Only a few people have seen it worldwide.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (2, 'Ivysaur', 'Grass', 'Poison', 60, 62, 63, 80, 60, 'When the bulb on its back grows large, it appears to lose the ability to stand on its hind legs.');
INSERT INTO Pokemon (pokedex_number, name, type1, type2, base_hp, base_attack, base_defense, base_special, base_speed, entry) VALUES (76, 'Golem', 'Rock', 'Ground', 80, 120, 130, 55, 45, 'Its boulder-like body is extremely hard. It can easily withstand dynamite blasts without damage.');